export default function LoginFinal() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch("http://localhost:4000/user/login", { // Fixed URL
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email, password }),
      });

      if (response.ok) {
        navigate("/user/home");  // Redirect on success
      } else {
        alert("Invalid credentials");
      }
    } catch (error) {
      console.error("Login error:", error);
    }
  };

  return (
    <main className='login'>
      <ModeToggle />
      <CssBaseline />
      <Sheet sx={{ width: 300, mx: 'auto', my: 4, py: 3, px: 2, display: 'flex', flexDirection: 'column', gap: 2, borderRadius: 'sm', boxShadow: 'md' }} variant="outlined">
        <div>
          <Typography level="h4" component="h1"><b>Welcome!</b></Typography>
          <Typography level="body-sm">Sign in to continue.</Typography>
        </div>
        <form onSubmit={handleLogin}>
          <FormControl>
            <FormLabel>Email</FormLabel>
            <Input name="email" type="email" placeholder="johndoe@email.com" onChange={(e) => setEmail(e.target.value)} />
          </FormControl>
          <FormControl>
            <FormLabel>Password</FormLabel>
            <Input name="password" type="password" placeholder="password" onChange={(e) => setPassword(e.target.value)} />
          </FormControl>
          <Button type="submit" sx={{ mt: 1 }}>Log in</Button>
        </form>
        <Typography endDecorator={<RouterLink to="/register">Register</RouterLink>} sx={{ fontSize: 'sm', alignSelf: 'center' }}>Don&apos;t have an account?</Typography>
      </Sheet>
    </main>
  );
}
