function RegisterFinal() {
  const [user, setUser] = useState({
    firstname: "",
    lastname: "",
    email: "",
    password: "",
  });

  const handleChange = (e) => {
    setUser({ ...user, [e.target.name]: e.target.value });
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    console.log(user);
    try {
      const response = await fetch(`http://localhost:4000/user/register`, {  // Fixed URL
        method: "POST",
        headers: {
          "Content-Type": "application/json",  // Fixed header issue
        },
        body: JSON.stringify(user),
      });

      if (response.ok) {
        alert("Registration successful");
      } else {
        alert("Registration failed");
      }
    } catch (error) {
      console.error("Error:", error);
    }
  };

  return (
    <main>
      <ModeToggle />
      <CssBaseline />
      <Sheet sx={{ width: 300, mx: 'auto', my: 4, py: 3, px: 2, display: 'flex', flexDirection: 'column', gap: 2, borderRadius: 'sm', boxShadow: 'md' }} variant="outlined">
        <div>
          <Typography level="h4" component="h1"><b>Create Account</b></Typography>
          <Typography level="body-sm">Register to get started.</Typography>
        </div>
        <form onSubmit={handleRegister}>
          <FormControl>
            <FormLabel>First Name</FormLabel>
            <Input name="firstname" type="text" placeholder="John" onChange={handleChange} />
          </FormControl>
          <FormControl>
            <FormLabel>Last Name</FormLabel>
            <Input name="lastname" type="text" placeholder="Doe" onChange={handleChange} />
          </FormControl>
          <FormControl>
            <FormLabel>Email</FormLabel>
            <Input name="email" type="email" placeholder="johndoe@email.com" onChange={handleChange} />
          </FormControl>
          <FormControl>
            <FormLabel>Password</FormLabel>
            <Input name="password" type="password" placeholder="password" onChange={handleChange} />
          </FormControl>
          <Button type="submit" sx={{ mt: 1 }}>Register</Button>
        </form>
        <Typography endDecorator={<Link href="/login">Login</Link>} sx={{ fontSize: 'sm', alignSelf: 'center' }}>Already have an account?</Typography>
      </Sheet>
    </main>
  );
}

export default RegisterFinal;
